from graphics import *import randomimport mathimport timedef main():    clicks = 0    size = 0    number = 0    while size < 300 or size > 600 :        size = int(eval(input("Size of Happy Valley  (300 - 600): ")))    while number < 2 or number > 20 :        number = int(eval(input("Number of Residents (2 - 20): ")))    field = GraphWin("Happy Valley",size,size)    balls = make(number,size)    draw(balls,field)    while clicks < 2:        click = field.checkMouse()        if click:            clicks += 1            for ball in balls:                if dist(click,ball[0].getCenter())<=12:                    ball[0].setFill('red')        for ball in balls:            ball = bounce(ball,size)            ball[0].move(ball[1],ball[2])        balls = collide(balls)        time.sleep(0.080)    field.close()def dist(p1, p2):    c = math.sqrt((p1.getX()-p2.getX())**2 + (p1.getY()-p2.getY())**2)    return cdef bounce(ball,size):    center = ball[0].getCenter()    if center.getX() >= (size-12) or center.getX() <= 12:        ball[1] = ball[1] * -1    if center.getY() >= (size-12) or center.getY() <=12:        ball[2] = ball[2] * -1    ball[0].move(ball[1],ball[2])    return balldef collide(balls):    for i in range(len(balls)-1):        for j in range(i+1,len(balls)):            if dist(balls[i][0].getCenter(),balls[j][0].getCenter()) < 21:                if (balls[i][1]>0 and balls[j][1]<0) or (balls[i][1]<0 and balls[j][1]>0):                    balls[i][1] *= -1                    balls[j][1] *= -1                if (balls[i][2]>0 and balls[j][2]<0) or (balls[i][2]<0 and balls[j][2]>0):                    balls[i][2] *= -1                    balls[j][2] *= -1                if (balls[i][0].config['fill'] == 'red') or (balls[j][0].config['fill'] == 'red'):                    balls[i][0].setFill('red')                    balls[j][0].setFill('red')    return ballsdef make(number,size):    colors = ['red','blue','green','purple','yellow']    balls = []    for i in range(number):        center = Point(int(random.random()*(size-65))+20,int(random.random()*(size-65))+20)        stepX = int(random.random()*25)-15        stepY = int(random.random()*25)-15        balls.append([Circle(center,10),stepX,stepY])        fill = colors[int(random.random()*4)]        balls[i][0].setFill(fill)    return ballsdef draw(balls,field):    for ball in balls:        ball[0].draw(field)main()